import { useState, useCallback } from 'react';
import { withRouter } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import axiox from 'axios';
// import swr from 'swr';

import { auth } from '../../firebase/config.ts';
import Layout from '../shared/layout';
import './sign-up.styles.scss';

const initialValues = {
  firstname: 'art',
  email: 'test@email.com',
  password: '123456'
}

// // SWr fetcher
// const fetcher = (...args) => fetch(...args).then(res => res.json())

const SignUp = ({ history }) => {
  // const [values, setValues] = useState(initialValues);
  const { register, handleSubmit, formState: { errors, isSubmitting, isDirty, isValid } } = useForm(initialValues);
  console.log('React app origin:', window.location.origin);

  const onSubmit = async (data) => {
    try {
      const serverUri = process.env.REACT_APP_SERVER_URI;
      console.log('server uri: ', serverUri);

      // Remove credentials temporarily
      // const result = await fetch(`https://api.restful-api.dev/objects`);

      const result = await fetch(`${serverUri}/api/signup`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(data),
        credentials: 'include'
      })

      console.log('result: ', result);
      console.log('result.status: ', result.status);
      console.log('result.ok: ', result.ok);

      if (!result.ok) throw new Error(`HTTP ${result.status}`);
      const message = await result.json();
      console.log('return user: ', message);

      history.push('/');
    } catch (error) {
      console.error('=== ERROR CAUGHT ===');
      console.error('Full error:', error);
    }
  };
  // const onSubmit = useCallback(async (data) => {
  //   try {
  //     const serverUri = process.env.REACT_APP_SERVER_URI;
  //     console.log('server uri: ', serverUri)
  //     const result = await fetch(`${serverUri}/api/signup`, {
  //       method: 'POST',
